var tickOverPower = 4; // need 4kW just to get to tomorrow
var reservePower = 4; // Keep 4kW just in case?

var diff = (generateTomorrow / batteryCapacity) * 100;

if (diff < 100) {
  // Battery cannot be fully charged tomorrow
  // Fill up to what should be enough to allow it
  // to fill from solar as much as possible.

  chargeToPercent = 50;
  // Too hard to work out.  Too tired.

} else if (diff >= 100) {
    // Tomorrow will produce more power than
    // the battery can take, so what do we do?
